import styled from "styled-components";

const HeaderContainer = styled.div`
    padding: 1.2em;
    font-size: large;
`;

const PostContainer = styled.div`
    text-align : center;
`

const BoxCotainer = styled.div`
    padding: 1em;
    display: flex;
    flex-flow: row nowrap;
    justify-content: space-around;

    input {
        width: 50vw;
        border-color: #ffffff;
    }
`

const PostButton = styled.button`
    width: 20vw;
    height: 5vh;
    border: 1px solid #ffffff;
    border-radius: 10px;
    background-color: #F7D358;

    color: #ffffff;
    font-weight: bold;
`

const RestCotainer = styled(BoxCotainer)`
    font-size: 90%;
    div {
        color: #696969;
    }
    select {
        width : 50vw;
    }
`

export { HeaderContainer, PostContainer, BoxCotainer, PostButton, RestCotainer }
