import React, { useState } from "react";
import { HeaderContainer, PostContainer, BoxCotainer, PostButton, RestCotainer } from "./styles";

const PostInput = () => {
    /*
    const [title, setTitle] = useState<string>("");
    const [restaurant, setRestaurant] = useState<string>("");
    const [category, setCategory] = useState<int>(0);
    const [minPrice, setMinPrice] = useState<int>(null);
    const [orderTime, setOrderTime] = useState<string>("");

    const handleCheckDuplicateRestaurant = () => {

    };
    */

    const handleAddFee = (order, fee) => {
        let ul = document.getElementsByTagName('ul')[1];
        let li = document.createElement('li');

        li.appendChild(document.createTextNode("order 이상 fee원"))
        console.log(ul);
        //ul.appendChild(li);
    }

    return (
        <PostContainer>
            <HeaderContainer>
                나눔 등록하기
            </HeaderContainer>
            <BoxCotainer>
                <div>제목</div>
                <input
                    placeholder="제목을 입력해주세요"
                />
            </BoxCotainer>
            <BoxCotainer>
                <div>매장</div>
                <input
                    placeholder="매장을 입력해주세요"
                />
            </BoxCotainer>
            <RestCotainer>
                <div>카테고리</div>
                <select name="category">
                    <option value="한식">한식</option>
                    <option value="중식">중식</option>
                    <option value="일식">일식</option>
                </select>
            </RestCotainer>
            <RestCotainer>
                <div>최소주문금액</div>
                <input
                    placeholder="금액을 입력해주세요"
                />
                <div>원</div>
            </RestCotainer>
            <RestCotainer>
                <div>배달비</div>
                <button onClick={handleAddFee()}>+</button>
                <ul></ul>
            </RestCotainer>
            <PostButton onClick={() => alert("등록완료")}>
                등록하기
            </PostButton>
        </PostContainer>
    )
};

export default PostInput
