import styled from "styled-components";

const HeaderContainer = styled.div`
    padding: 1.2em;
    font-size: large;
`;

const PostContainer = styled.div`
    text-align : center;
`

const BoxCotainer = styled.div`
    padding: 1em;
    display: flex;
    flex-flow: row nowrap;
    justify-content: space-around;

    div {
        color: #696969;
    }
    input {
        width: 50vw;
        border-color: #ffffff;
    }
`

const PostButton = styled.button`
    width: 20vw;
    height: 5vh;

    border: 1px solid;
    border-radius: 10px;
    background-color: #F7D358;
`

export { HeaderContainer, PostContainer, BoxCotainer, PostButton }
